mapper.select:
select

from t_op_reality_account
where 1=1

  and CACHE_TYPE_CD = #{cacheTypeCd}


  and CACHE_DATA_TYPE_CD = #{cacheDataTypeCd,jdbcType=VARCHAR}

and VALID_FLAG = '1'

	select REAL_ACCT_ID, REQST_SRC_CODE, REQST_SRC_NM, USER_NM, PW, AES_KEY, DES_KEY, 
  INTF_PRIV_CODE, VALID_FLAG, CACHE_TYPE_CD, BIZ_SYS_CODE, CRT_USER_ID, CRT_TIME, CRT_APP_SYS_ID, 
  MODF_USER_ID, MODIFY_TIME, MODIFY_APP_SYS_ID, CACHE_DATA_TYPE_CD, PUSH_ALERT_FLAG,CACHE_KEY_VAL
  from t_op_reality_account


  	REAL_ACCT_ID = #{realAcctId,jdbcType=BIGINT}


  	and REQST_SRC_CODE = #{reqstSrcCode,jdbcType=VARCHAR}


  	and REQST_SRC_NM = #{reqstSrcNm,jdbcType=VARCHAR}


  	and USER_NM = #{userNm,jdbcType=VARCHAR}


  	and PW = #{pw,jdbcType=VARCHAR}


  	and AES_KEY = #{aesKey,jdbcType=VARCHAR}


  	and DES_KEY = #{desKey,jdbcType=VARCHAR}


  	and INTF_PRIV_CODE = #{intfPrivCode,jdbcType=VARCHAR}


  	and CACHE_TYPE_CD = #{cacheTypeCd,jdbcType=VARCHAR}


  	and BIZ_SYS_CODE = #{bizSysCode,jdbcType=VARCHAR}


  	and CACHE_DATA_TYPE_CD = #{cacheDataTypeCd,jdbcType=VARCHAR}


  	and PUSH_ALERT_FLAG = #{pushAlertFlag,jdbcType=VARCHAR}


  	and CACHE_KEY_VAL = #{cacheKeyVal,jdbcType=VARCHAR}

  and VALID_FLAG = '1'

mapper.delete:
  	update t_op_reality_account
  	set VALID_FLAG = '0'
  	and MODF_USER_ID = #{modfUserId,jdbcType=VARCHAR}
    and MODIFY_TIME = #{modifyTime,jdbcType=TIMESTAMP}
    and MODIFY_APP_SYS_ID = #{modifyAppSysId,jdbcType=VARCHAR}	


    		REAL_ACCT_ID = #{realAcctId,jdbcType=BIGINT}


    		and REQST_SRC_CODE = #{reqstSrcCode,jdbcType=VARCHAR}


    		and REQST_SRC_NM = #{reqstSrcNm,jdbcType=VARCHAR}


    		and USER_NM = #{userNm,jdbcType=VARCHAR}


    		and PW = #{pw,jdbcType=VARCHAR}


    		and AES_KEY = #{aesKey,jdbcType=VARCHAR}


    		and DES_KEY = #{desKey,jdbcType=VARCHAR}


	    	and INTF_PRIV_CODE = #{intfPrivCode,jdbcType=VARCHAR}


	    	and CACHE_TYPE_CD = #{cacheTypeCd,jdbcType=VARCHAR}


	    	and BIZ_SYS_CODE = #{bizSysCode,jdbcType=VARCHAR}


	    	and CACHE_DATA_TYPE_CD = #{cacheDataTypeCd,jdbcType=VARCHAR}


	    	and PUSH_ALERT_FLAG = #{pushAlertFlag,jdbcType=VARCHAR}


	    	and CACHE_KEY_VAL = #{cacheKeyVal,jdbcType=VARCHAR}
